request the information of a user (vocab.query:
PREFIX rdf: <http://www.w3.org/1999/02/22-rdf-syntax-ns#>
PREFIX snvoc: <http://localhost:3000/www.ldbc.eu/ldbc_socialnet/2.0/vocabulary/>

SELECT ?firstName ?lastName ?birthday ?locationIP ?browserUsed ?cityId ?gender ?creationDate
WHERE {
  <http://localhost:3000/pods/00000000000000000933/profile/card#me>
      rdf:type snvoc:Person ;
      snvoc:id ?personId ;
      snvoc:firstName ?firstName ;
      snvoc:lastName ?lastName ;
      snvoc:gender ?gender ;
      snvoc:birthday ?birthday ;
      snvoc:creationDate ?creationDate ;
      snvoc:locationIP ?locationIP ;
      snvoc:isLocatedIn ?city ;
      snvoc:browserUsed ?browserUsed .

  ?city snvoc:id ?cityId .
}

