@prefix semmap: <https://semanticmapping.org/vocab#> .
@prefix skos: <http://www.w3.org/2004/02/skos/core#> .
@prefix snvocv1: <http://localhost:3000/www.ldbc.eu/ldbc_socialnet/1.0/vocabulary/> .
@prefix snvocv2: <http://localhost:3000/www.ldbc.eu/ldbc_socialnet/2.0/vocabulary/> .

[]
    a semmap:RuleSet ;
    semmap:subweb "*" ;
    semmap:rule
        [
            semmap:premise snvocv1:Person ;
            semmap:inference skos:exactMatch ;
            semmap:conclusion snvocv2:Person
        ],
        [
            semmap:premise snvocv1:id ;
