            semmap:inference skos:exactMatch ;
            semmap:conclusion snvocv2:lastName
        ],
        [
            semmap:premise snvocv1:gender ;
            semmap:inference skos:exactMatch ;
            semmap:conclusion snvocv2:gender
        ],
        [
            semmap:premise snvocv1:birthday ;
            semmap:inference skos:exactMatch ;
