SELECT DISTINCT ?forumId ?forumTitle
WHERE {
  ?message snvoc:fromAuthor <http://localhost:3000/pods/00000000000000001274/profile/card#me> .
  ?forum snvoc:containerOf ?message ;
         snvoc:id ?forumId ;
         snvoc:title ?forumTitle .
